INSERT INTO `pages` (`id`, `title`, `content`, `slug`, `priority`) VALUES
(1, 'About', '<p>We are FreshAir.org.uk, bringing you student radio from Scotland&#39;s capital!</p>\r\n\r\n<p>Active since 1991, we&rsquo;re one of the university&rsquo;s biggest societies and one of the city&rsquo;s most diverse radio stations, broadcasting everything from entertainment and chat shows to debate, new music, local news and arts coverage.</p>\r\n\r\n<p>With over 300 members and 10,000 listeners per month, FreshAir has something for everyone whether you&rsquo;re interested in joining us or just want to listen in.</p>\r\n\r\n<p>Catch us online 24/7, on your smartphone via the TuneIn app, or&nbsp;<strong>playing out in some of your favourite unions and local shops, bars and cafes!</strong></p>\r\n', 'about', NULL),
(2, 'Members', '<p>Welcome to our members pages</p>\r\n', 'members', NULL);
